const JSON = require('json5');
const BlockType = require('../../extension-support/block-type');
const ArgumentType = require('../../extension-support/argument-type');
const Scratch3RosBase = require('./RosUtil');

const icon = 'data:image/svg+xml;base64,'

class Scratch3RosBlocks extends Scratch3RosBase {

    get TOGGLE_MENU() {
        return [
            {
                text: 'ON',
                value: '1'
            },
            {
                text: 'OFF',
                value: '0'
            },
        ];
    }

    constructor(runtime) {
        super('ROS', 'ros', runtime);
        this.start = false;
        this.receiveIt = (msg) => {
            if (msg != null) {
                this.msg = msg.data;
                this.start = true;
            }
        }
    }


    followMe({ STATE }) {
        const ROS = this.ros;
        ROS.getTopic("/scratch/state_change").then(rosTopic => {
            var state;
            if (STATE === "1") {
                state = true;
            } else {
                state = false;
            }
            if (!rosTopic.name) return;
            if (rosTopic.messageType === 'scratch_msgs/StateChange') {
                msg = {
                    target: 'hsr_follower',
                    enable: state,
                    detail: ''
                };
                rosTopic.publish(msg);
            }
        });
    }

    receiveMessage({VAR}, util) {
        if (this.start && this.msg != null) {
            this.start = false;
            const variable = util.target.lookupVariableByNameAndType(VAR);
            variable.value = this.msg;
            if (variable.isCloud) {
                util.ioQuery('cloud', 'requestUpdateVariable', [variable.name, this.msg]);
            }
            return true;
        }
        return this.start;
    }

    startReceiveMessage({ }) {
        if (!this.topic) {
            this.start = true;
            this.topic = this.ros.createTopic("/scratch/from_operator", "std_msgs/String");
            this.topic.subscribe(this.receiveIt);
        }
    }

    endReceiveMessage({ }) {
        this.start = false;
        if (this.topic) {
            this.topic.unsubscribe(this.receiveIt);
            this.topic = null;
        }
    }

    goXYRobot({ X, Y }) {
        this.ros.goMove(X, Y);
    }

    goToRobot({ WHERE }) {
        switch (WHERE) {
            case "table1":
                this.ros.goMove(0.83758020401, -3.56895685196, 0.997537440299, 0.0701359765147);
                return;
            case "table2":
                this.ros.goMove(0.807728290558, -3.51406812668, -0.0623495318673, 0.99805437521);
                return;
            case "table3":
                this.ros.goMove(2.88800430298, -3.75257873535, -0.0670893964195, 0.997746968368);
                return;
            case "sink":
                this.ros.goMove(5.893180370331, -3.66580319405, -0.00878589193078, 0.999961403307);
                return;
            case "refrigerator":
                this.ros.goMove(5.75406360626, -0.322618514299, 0.0034197094787, 0.999994152776);
                return;
        }
    }
    // customize to handle topics advertised from Scratch
    subscribeTopic({ TOPIC }) {
        const that = this;
        return new Promise(resolve => {
            that.ros.getTopic(TOPIC).then(
                rosTopic => {
                    if (!rosTopic.messageType) resolve();
                    rosTopic.subscribe(msg => {
                        // rosTopic.unsubscribe();
                        if (rosTopic.messageType === 'std_msgs/String') {
                            msg.data = that._tryParse(msg.data, msg.data);
                        }
                        msg.toString = function () { return JSON.stringify(this); };
                        msg.constructor = Object;
                        resolve(msg);
                    });
                });
        });
    }

    // customize to handle unadvertised topics
    publishTopic({ MSG, TOPIC }, util) {
        const ROS = this.ros;
        let msg = this._getVariableValue(MSG, util.target);
        if (msg === null || typeof msg === 'undefined') msg = this._tryParse(MSG);
        if (!this._isJSON(msg)) msg = { data: msg };

        ROS.getTopic(TOPIC).then(rosTopic => {
            if (!rosTopic.name) return;
            const keys = Object.keys(msg);
            if (rosTopic.messageType) {
                if (rosTopic.messageType === 'std_msgs/String' &&
                    !(keys.length === 1 && keys[0] === 'data')) {
                    msg = { data: JSON.stringify(msg) };
                }
            } else {
                if (!(keys.length === 1 && keys[0] === 'data')) {
                    msg = { data: JSON.stringify(msg) };
                }
                rosTopic.messageType = ROS.getRosType(msg.data);
            }
            rosTopic.publish(msg);
        });
    }

    callService({ REQUEST, SERVICE }, util) {
        const req = this._getVariableValue(REQUEST, util.target) || this._tryParse(REQUEST);
        return this.ros.callService(SERVICE, req);
    }

    getParamValue({ NAME }) {
        const that = this;
        return new Promise(resolve => {
            const param = that.ros.getParam(NAME);
            param.get(val => {
                if (val === null) resolve();
                if (that._isJSON(val)) {
                    val.toString = function () { return JSON.stringify(this); };
                }
                resolve(val);
            });
        });
    }

    setParamValue({ NAME, VALUE }) {
        const param = this.ros.getParam(NAME);
        const val = Array.isArray(VALUE) ?
            VALUE.map(v => this._tryParse(v, v)) :
            this._tryParse(VALUE, VALUE);
        param.set(val);
    }

    getSlot({ OBJECT, SLOT }, util) {
        const evalSlot = function (obj, slots) {
            const slotArr = slots.split(/\.|\[|\]/).filter(Boolean);
            for (let i = 0; i < slotArr.length; i++) {
                if (!obj) return;
                obj = obj[slotArr[i]];
            }
            return obj;
        };

        const variable = util.target.lookupVariableByNameAndType(OBJECT);
        const obj = this._getVariableValue(variable) || this._tryParse(OBJECT);
        const res = (this._isJSON(obj) || void 0) && evalSlot(obj, SLOT);
        if (util.thread.updateMonitor) {
            if (typeof res === 'undefined') {
                const name = `${OBJECT}.${SLOT}`;
                const id = variable ?
                    variable.id + name :
                    Object.keys(util.runtime.monitorBlocks._blocks)
                        .find(key => key.search(name) >= 0);

                util.runtime.monitorBlocks.deleteBlock(id);
                util.runtime.requestRemoveMonitor(id);
            } else return JSON.stringify(res);
        }
        if (this._isJSON(res)) res.toString = function () { return JSON.stringify(this); };
        return res;
    }

    setSlot({ VAR, SLOT, VALUE }, util) {
        const setNestedValue = function (obj, slots, value) {
            const last = slots.length - 1;
            for (let i = 0; i < last; i++) {
                const slot = slots[i];
                if (!obj.hasOwnProperty(slot) || typeof obj[slot] !== 'object') {
                    obj[slot] = isNaN(parseInt([slots[i + 1]])) ? {} : [];
                }
                obj = obj[slot];
            }
            obj[slots[last]] = value;
        };

        const variable = util.target.lookupVariableByNameAndType(VAR);
        if (!variable) return;
        const variableValue = this._getVariableValue(variable);

        if (this._isJSON(variableValue)) {
            // Clone object to avoid overwriting parent variables
            variable.value = JSON.parse(JSON.stringify(variableValue));
        } else variable.value = {};
        const slt = SLOT.split(/\.|\[|\]/).filter(Boolean);
        const val = Array.isArray(VALUE) ?
            VALUE.map(v => this._tryParse(v, v)) :
            this._tryParse(VALUE, VALUE);

        setNestedValue(variable.value, slt, val);

        // TODO: cloud variables
    }

    showVariable(args) {
        this._changeVariableVisibility(args, true);
    }

    hideVariable(args) {
        this._changeVariableVisibility(args, false);
    }

    solveFormula({ EXPRESSION, OBJECT }, util) {
        const obj = this._getVariableValue(OBJECT, util.target) || this._tryParse(OBJECT);
        let binds;
        if (this._isJSON(obj)) {
            binds = JSON.parse(JSON.stringify(obj));
            delete binds.toString;
            delete binds.constructor;
        } else {
            binds = {};
            binds[OBJECT] = obj;
        }

        try {
            const result = this.math.eval(EXPRESSION, binds);
            if (this.math.typeof(result) === 'Unit') return result.toNumber();
            return result;
        } catch (err) {
            return;
        }
    }

    getInfo() {
        const stringArg = defValue => ({
            type: ArgumentType.STRING,
            defaultValue: defValue
        });
        const variableArg = {
            type: ArgumentType.STRING,
            menu: 'variablesMenu',
            defaultValue: this._updateVariableList()[0].text
        };
        const topicArg = {
            type: ArgumentType.STRING,
            menu: 'topicsMenu',
            defaultValue: this.topicNames[0]
        };
        const serviceArg = {
            type: ArgumentType.STRING,
            menu: 'servicesMenu',
            defaultValue: this.serviceNames[0]
        };
        const paramArg = {
            type: ArgumentType.STRING,
            menu: 'paramsMenu',
            defaultValue: this._updateParamList()[0].text
        };

        return {
            id: this.extensionId,
            name: this.extensionName,
            showStatusButton: true,

            colour: '#8BC34A',
            colourSecondary: '#7CB342',
            colourTertiary: '#689F38',

            menuIconURI: icon,
            blockIconURI: icon,

            blocks: [
                {
                    opcode: 'receiveMessage',
                    text: 'receive [MESSAGE] and put into [VAR]',
                    blockType: BlockType.HAT,
                    arguments: {
                        MESSAGE: {
                            menu: 'messageMenu',
                            type: ArgumentType.STRING,
                            defaultValue: 'from_operator'
                        },
                        VAR: variableArg
                    }
                },
                {
                    opcode: 'startReceiveMessage',
                    text: 'start [MESSAGE]',
                    blockType: BlockType.COMMAND,
                    arguments: {
                        MESSAGE: {
                            menu: 'messageMenu',
                            type: ArgumentType.STRING,
                            defaultValue: 'from_operator'
                        }
                    }
                },
                {
                    opcode: 'endReceiveMessage',
                    text: 'stop [MESSAGE]',
                    blockType: BlockType.COMMAND,
                    arguments: {
                        MESSAGE: {
                            menu: 'messageMenu',
                            type: ArgumentType.STRING,
                            defaultValue: 'from_operator'
                        }
                    }
                },
                '---',
                {
                    opcode: 'followMe',
                    blockType: BlockType.COMMAND,
                    text: 'change Follow me into [STATE]',
                    arguments: {
                        STATE: {
                            menu: 'toggleMenu',
                            type: ArgumentType.STRING,
                            defaultValue: '1'
                        }
                    }
                },
                {
                    opcode: 'goXYRobot',
                    blockType: BlockType.COMMAND,
                    text: 'go to x coordinates [X]、y coordinates [Y]',
                    arguments: {
                        X: {
                            type: ArgumentType.NUMBER,
                            defaultValue: 0
                        },
                        Y: {
                            type: ArgumentType.NUMBER,
                            defaultValue: 0
                        },
                    }
                },
                {
                    opcode: 'goToRobot',
                    blockType: BlockType.COMMAND,
                    text: 'go to [WHERE]',
                    arguments: {
                        WHERE: {
                            menu: 'areaNameMenu',
                            type: ArgumentType.STRING,
                        }
                    }
                },
                '---',
                {
                    opcode: 'subscribeTopic',
                    blockType: BlockType.REPORTER,
                    text: 'Get message from [TOPIC]',
                    arguments: {
                        TOPIC: topicArg
                    }
                },
                {
                    opcode: 'publishTopic',
                    blockType: BlockType.COMMAND,
                    text: 'Publish [MSG] to [TOPIC]',
                    arguments: {
                        MSG: variableArg,
                        TOPIC: topicArg
                    }
                },
                '---',
                {
                    opcode: 'callService',
                    blockType: BlockType.REPORTER,
                    text: 'Send [REQUEST] to [SERVICE]',
                    arguments: {
                        REQUEST: variableArg,
                        SERVICE: serviceArg
                    }
                },
                '---',
                {
                    opcode: 'getParamValue',
                    blockType: BlockType.REPORTER,
                    text: 'Get rosparam [NAME]',
                    arguments: {
                        NAME: paramArg
                    }
                },
                {
                    opcode: 'setParamValue',
                    blockType: BlockType.COMMAND,
                    text: 'Set rosparam [NAME] to [VALUE]',
                    arguments: {
                        NAME: paramArg,
                        VALUE: stringArg(0)
                    }
                },
                '---',
                {
                    opcode: 'getSlot',
                    blockType: BlockType.REPORTER,
                    text: 'Get [OBJECT] [SLOT]',
                    arguments: {
                        OBJECT: variableArg,
                        SLOT: stringArg('data')
                    }
                },
                {
                    opcode: 'setSlot',
                    blockType: BlockType.COMMAND,
                    text: 'Set [VAR] [SLOT] to [VALUE]',
                    arguments: {
                        VAR: variableArg,
                        SLOT: stringArg('data'),
                        VALUE: stringArg('Hello!')
                    }
                },
                {
                    opcode: 'showVariable',
                    blockType: BlockType.COMMAND,
                    text: 'Show [VAR] [SLOT]',
                    arguments: {
                        VAR: variableArg,
                        SLOT: stringArg('data')
                    }
                },
                {
                    opcode: 'hideVariable',
                    blockType: BlockType.COMMAND,
                    text: 'Hide [VAR] [SLOT]',
                    arguments: {
                        VAR: variableArg,
                        SLOT: stringArg('data')
                    }
                },
                '---',
                {
                    opcode: 'solveFormula',
                    blockType: BlockType.REPORTER,
                    text: '[EXPRESSION] binding [OBJECT]',
                    arguments: {
                        EXPRESSION: stringArg('(data + 1) ^ 2'),
                        OBJECT: variableArg
                    }
                }
            ],
            menus: {
                topicsMenu: '_updateTopicList',
                servicesMenu: '_updateServiceList',
                variablesMenu: '_updateVariableList',
                paramsMenu: '_updateParamList',
                toggleMenu: {
                    acceptReporters: false,
                    items: this.TOGGLE_MENU
                },
                messageMenu: {
                    acceptReporters: false,
                    items: [
                        {
                            text: '人からのメッセージ',
                            value: 'from_operator'
                        },
                    ]
                },
                areaNameMenu: {
                    acceptReporters: false,
                    items: [
                        {
                            text: 'table1',
                            value: 'table1'
                        },
                        {
                            text: 'table2',
                            value: 'table2'
                        },
                        {
                            text: 'table3',
                            value: 'table3'
                        },
                        {
                            text: 'sink',
                            value: 'sink'
                        },
                        {
                            text: 'refrigerator',
                            value: 'refrigerator'
                        },
                    ]
                }
            }
        };
    }
}

module.exports = Scratch3RosBlocks;
